<?php

use App\Models\Article;
use App\Models\Day;
use App\Models\EventReport;
use App\Services\ImageService;
use Backpack\Settings\app\Models\Setting;
use Illuminate\Foundation\Testing\RefreshDatabase;

uses(RefreshDatabase::class);


it('it resize the image', function () {
    $base64Image = "data:image/png;base64,";
    $image = \Image::make($base64Image)->encode('png', 100);

    $setting = new Setting();
    $setting->key = 'default_image_width';
    $setting->value = 500;
    $setting->field = '';
    $setting->name = 'adi';
    $setting->active = 1;
    $setting->save();

    $setting = new Setting();
    $setting->key = 'default_image_height';
    $setting->value = 500;
    $setting->field = '';
    $setting->name = 'adi';
    $setting->active = 1;
    $setting->save();

    $article = Day::factory()->create();
    $service = new ImageService($base64Image, $article);


    $service->imageUpload();
    $currentImage = $service->getProcessedImage();

    expect($currentImage->getSize()->width)->toBe(500);
    expect($currentImage->getSize()->height)->toBe(500);
});
